package com.operators;

public class IncDec_TC3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		int y= ++x;
		System.out.println("x :"+x);
		System.out.println("y :"+ y);
		System.out.println();
		
		x=10;
		y=x++;
		System.out.println("x :"+ x);
		System.out.println("y :"+ y);
		
		x=10;
		y=x++;
		System.out.println("x :"+ y);
		
	}

}