package com.string.operations;

public class StartswithEndsWith11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s="Abc def bca";
		
		System.out.println(s.startsWith("abc"));
		System.out.println(s.startsWith("abcdef"));
		System.out.println(s.endsWith("bca"));
		System.out.println(s.endsWith("defbca"));
		
	}

}
